# ========================================
# ||      Module Install / Update       ||
# ========================================

function Install-RequiredModules {

    Add-Type -AssemblyName System.Windows.Forms
    Add-Type -AssemblyName System.Drawing

    # === Prompt user whether to check for updates ===
    $result = [System.Windows.Forms.MessageBox]::Show(
        "Do you want to check for newer versions of installed modules?" + `
        "`r`n`r`nYes = Check and update modules (slower)" + `
        "`r`nNo  = Only install missing modules (faster)",
        "Module Updates",
        [System.Windows.Forms.MessageBoxButtons]::YesNo,
        [System.Windows.Forms.MessageBoxIcon]::Question
    )
    $CheckUpdates = ($result -eq [System.Windows.Forms.DialogResult]::Yes)

    # === Module list ===
    $mods = @(
        "PackageManagement",
        "PowerShellGet",
        "Microsoft.Graph.Authentication",
        "Az.Accounts",
        "Az.Resources",
        "Az.Storage",
        "ExchangeOnlineManagement",
        "Microsoft.Graph.Applications",        
        "Microsoft.Graph.Identity.DirectoryManagement",
        "Microsoft.Graph.Groups",
        "Microsoft.Graph.Users",
        "Microsoft.Graph.Users.Actions",
        "Microsoft.Graph.Beta.Groups",
	    "Microsoft.Graph.Beta.Identity.DirectoryManagement",
        "Microsoft.Online.SharePoint.PowerShell",
        "MicrosoftTeams",
        "PnP.PowerShell"
    )

    # === Progress form setup ===
    $progressForm = New-Object System.Windows.Forms.Form
    $progressForm.Text = "Preparing Environment"
    $progressForm.StartPosition = "CenterScreen"
    $progressForm.Size = New-Object System.Drawing.Size(520,200)
    $progressForm.FormBorderStyle = 'FixedDialog'
    $progressForm.ControlBox = $false
    $progressForm.TopMost = $true

    $label = New-Object System.Windows.Forms.Label
    $label.Text = "Installing/Updating Required Modules..."
    $label.AutoSize = $true
    $label.Font = New-Object System.Drawing.Font("Segoe UI", 10, [System.Drawing.FontStyle]::Bold)
    $label.Location = New-Object System.Drawing.Point(10,10)
    $progressForm.Controls.Add($label)

    $progressBar = New-Object System.Windows.Forms.ProgressBar
    $progressBar.Width = 480
    $progressBar.Height = 24
    $progressBar.Location = New-Object System.Drawing.Point(10,40)
    $progressBar.Minimum = 0
    $progressBar.Maximum = 100
    $progressForm.Controls.Add($progressBar)

    $statusLabel = New-Object System.Windows.Forms.Label
    $statusLabel.Text = "Starting..."
    $statusLabel.AutoSize = $true
    $statusLabel.Font = New-Object System.Drawing.Font("Segoe UI", 9)
    $statusLabel.Location = New-Object System.Drawing.Point(10,75)
    $progressForm.Controls.Add($statusLabel)

    $summaryLabel = New-Object System.Windows.Forms.Label
    $summaryLabel.Text = "0 of $($mods.Count) modules completed"
    $summaryLabel.AutoSize = $true
    $summaryLabel.Font = New-Object System.Drawing.Font("Segoe UI", 8)
    $summaryLabel.Location = New-Object System.Drawing.Point(10,95)
    $progressForm.Controls.Add($summaryLabel)

    $progressForm.Show()
    $progressForm.BringToFront()
    [System.Windows.Forms.Application]::DoEvents()

    # --- Trust PSGallery repo ---
    try {
        $repo = Get-PSRepository -Name PSGallery -ErrorAction Stop
        if ($repo.InstallationPolicy -ne "Trusted") {
            Set-PSRepository -Name PSGallery -InstallationPolicy Trusted
        }
    } catch {}
    if (-not (Get-PackageProvider -Name NuGet -ErrorAction SilentlyContinue)) {
        try { Install-PackageProvider -Name NuGet -Force -Scope CurrentUser -ErrorAction Stop } catch {}
    }

    # --- Stopwatch for ETA ---
    $stopwatch = [System.Diagnostics.Stopwatch]::StartNew()
    $lastElapsed = 0.0
    $emaSecondsPerModule = $null
    $alpha = 0.5

    # --- Main loop ---
    for ($i = 0; $i -lt $mods.Count; $i++) {
        $m = $mods[$i]
        $percentStart = [math]::Floor(($i / $mods.Count) * 100)
        $progressBar.Value = [math]::Min($percentStart, 100)
        $statusLabel.Text = "Processing $m..."
        $summaryLabel.Text = "$i of $($mods.Count) modules completed"
        $progressForm.Refresh()
        [System.Windows.Forms.Application]::DoEvents()

        try {
            $installed = Get-InstalledModule $m -ErrorAction SilentlyContinue

            if (-not $installed) {
                # --- Module missing: install it ---
                $statusLabel.Text = "Installing $m..."
                Install-Module $m -Scope CurrentUser -Force -ErrorAction Stop | Out-Null
                $statusLabel.Text = "✅ Installed $m"
            }
            elseif ($CheckUpdates) {
                # --- Check for updates only if user chose Yes ---
                $latest = Find-Module $m -Repository PSGallery -ErrorAction SilentlyContinue
                if ($latest -and $installed.Version -lt $latest.Version) {
                    $statusLabel.Text = "Updating $m ($($installed.Version) -`> $($latest.Version))..."
                    Update-Module $m -Force -Scope CurrentUser -ErrorAction Stop | Out-Null
                    $statusLabel.Text = "⬆️ Updated $m"
                } else {
                    $statusLabel.Text = "⏭ $m is up-to-date"
                }
            }
            else {
                # --- Skip check ---
                $statusLabel.Text = "⏭ Skipping version check for $m (already installed)"
            }
        } catch {
            $statusLabel.Text = "⚠️ Error with {0}: {1}" -f $m, $_.Exception.Message
        }

        # --- Update progress ---
        $percentEnd = [math]::Round((($i+1) / $mods.Count) * 100)
        $progressBar.Value = [math]::Min($percentEnd, 100)
        $summaryLabel.Text = "$($i+1) of $($mods.Count) modules completed"
        $progressForm.Refresh()
        [System.Windows.Forms.Application]::DoEvents()
    }

    # --- Finish ---
    $progressBar.Value = 100
    $statusLabel.Text = "✅ All modules processed."
    $summaryLabel.Text = "$($mods.Count) of $($mods.Count) modules completed"
    $progressForm.Refresh()
    Start-Sleep -Seconds 2
    $progressForm.Close()
}

function AutoRun-PnPAutomationCheck {

    $scriptPath = Join-Path $basePath "Entra\Check_PnP_Automation.ps1"

    if (-not (Test-Path $scriptPath)) {
        return
    }

    $proc = Start-Process `
        -FilePath "pwsh.exe" `
        -ArgumentList "-NoLogo -NoProfile -ExecutionPolicy Bypass -File `"$scriptPath`"" `
        -WindowStyle Normal `
        -Wait `
        -PassThru

    # If the user closed the window or the script failed → exit everything
    if ($proc.ExitCode -ne 0) {
        [System.Windows.Forms.MessageBox]::Show(
            "CloudDonny cannot continue because the required PnP setup was not completed.",
            "Setup Cancelled",
            [System.Windows.Forms.MessageBoxButtons]::OK,
            [System.Windows.Forms.MessageBoxIcon]::Error
        )

        [System.Environment]::Exit(1)
    }
}

