# Get latest PowerShell 7 MSI info from GitHub
function Get-LatestPowerShellMsi {
    try {
        $release = Invoke-RestMethod `
            -Uri "https://api.github.com/repos/PowerShell/PowerShell/releases/latest" `
            -Headers @{ "User-Agent" = "CloudDonny-Launcher" }

        $asset = $release.assets |
            Where-Object { $_.name -match 'PowerShell-.*-win-x64\.msi$' } |
            Select-Object -First 1

        if (-not $asset) {
            throw "Could not find win-x64 MSI in latest release"
        }

        return @{
            Version = $release.tag_name.TrimStart('v')
            Url     = $asset.browser_download_url
            Name    = $asset.name
        }
    }
    catch {
        throw "Failed to query GitHub for latest PowerShell release: $($_.Exception.Message)"
    }
}

# Install Powershell 7 if it does not exist
function Ensure-PowerShell7 {
    param(
        [Parameter(Mandatory)]
        [System.Windows.Forms.TextBox]$OutputBox
    )

    $pwshPath = "C:\Program Files\PowerShell\7\pwsh.exe"

    # Get installed version (if any)
    $installedVersion = $null
    if (Test-Path $pwshPath) {
        try {
            $installedVersion = & $pwshPath -NoLogo -NoProfile -Command '$PSVersionTable.PSVersion.ToString()'
        } catch {}
    }

    # Get latest version info from GitHub
    try {
        $latest = Get-LatestPowerShellMsi
    }
    catch {
        $OutputBox.AppendText("[ERROR] $($_)`r`n")
        return $null
    }

    $OutputBox.AppendText("[INFO] Latest PowerShell version on GitHub: $($latest.Version)`r`n")

    if ($installedVersion -and ([Version]$installedVersion -ge [Version]$latest.Version)) {
        $OutputBox.AppendText("[INFO] PowerShell 7 is already up to date ($installedVersion).`r`n")
        return $pwshPath
    }

    $choice = [System.Windows.Forms.MessageBox]::Show(
        "PowerShell 7 $($latest.Version) is required.`r`n`r`nInstall or upgrade now?",
        "PowerShell 7 Required",
        [System.Windows.Forms.MessageBoxButtons]::YesNo,
        [System.Windows.Forms.MessageBoxIcon]::Question
    )

    if ($choice -ne [System.Windows.Forms.DialogResult]::Yes) {
        $OutputBox.AppendText("[ERROR] PowerShell 7 installation cancelled.`r`n")
        return $null
    }

    $msiPath = Join-Path $env:TEMP $latest.Name

    try {
        $OutputBox.AppendText("[INFO] Downloading PowerShell 7 $($latest.Version)...`r`n")
        Invoke-WebRequest -Uri $latest.Url -OutFile $msiPath -UseBasicParsing

        $OutputBox.AppendText("[INFO] Installing PowerShell 7 (machine-wide)...`r`n")
        Start-Process msiexec.exe `
            -ArgumentList "/i `"$msiPath`" /qn ADD_PATH=1 ENABLE_PSREMOTING=1" `
            -Verb RunAs `
            -Wait

        if (Test-Path $pwshPath) {
            $OutputBox.AppendText("[SUCCESS] PowerShell 7 $($latest.Version) installed successfully.`r`n")
            return $pwshPath
        }

        throw "Installation completed but pwsh.exe not found"
    }
    catch {
        $OutputBox.AppendText("[ERROR] PowerShell 7 installation failed: $($_.Exception.Message)`r`n")
        return $null
    }
    finally {
        if (Test-Path $msiPath) {
            Remove-Item $msiPath -Force -ErrorAction SilentlyContinue
        }
    }
}

# Used for Powershell 7 scripts
function Invoke-Pwsh7Script {
    param (
        [Parameter(Mandatory)]
        [string]$ScriptPath,

        [Parameter(Mandatory)]
        [System.Windows.Forms.TextBox]$OutputBox
    )

    try {
        # Ensure latest PowerShell 7 and get pwsh.exe path
        $PwshPath = Ensure-PowerShell7 -OutputBox $OutputBox
        if (-not $PwshPath) { return }

        if (-not (Test-Path $ScriptPath)) {
            $OutputBox.AppendText("[ERROR] Script not found: $ScriptPath`r`n")
            return
        }

        $OutputBox.AppendText("[INFO] Launching script in PowerShell 7...`r`n")

        $args = @(
            '-NoLogo',
            '-NoProfile',
            '-ExecutionPolicy', 'Bypass',
            '-Command',
            "& { & '$ScriptPath'; Write-Host ''; Write-Host '--- Press any key to close ---' -ForegroundColor Yellow; Pause }"
        )

        Start-Process -FilePath $PwshPath -ArgumentList $args -WindowStyle Normal
        $OutputBox.AppendText("[INFO] PowerShell 7 window opened successfully.`r`n")
    }
    catch {
        $OutputBox.AppendText("[ERROR] Failed to launch PowerShell 7 script: $($_.Exception.Message)`r`n")
    }
}

