# ============================================================
# ||     Helper: Assign App Icon to Any Window Form          ||
# ============================================================

function Set-WindowIcon {
    param([System.Windows.Forms.Form]$Form)

    try {
        # Use the icon from the running EXE if compiled
        $exePath = [System.Diagnostics.Process]::GetCurrentProcess().MainModule.FileName
        $Form.Icon = [System.Drawing.Icon]::ExtractAssociatedIcon($exePath)
    }
    catch {
        # Fallback to a .ico file (useful in dev/testing .ps1 mode)
        $iconPath = Join-Path $basePath "CloudDonny.ico"
        if (Test-Path $iconPath) {
            try { $Form.Icon = New-Object System.Drawing.Icon($iconPath) } catch {}
        }
    }
}

