# ========================================
# ||          Scheme Outputs            ||
# ========================================

function Format-ColoredLine {
    param([string]$Text)

    switch -Regex ($Text) {
        '✅|success|created|done|completed' {
            $type = 'SUCCESS'
            $desc = 'Operation completed successfully'
            break
        }
        '⚠️|already exists|skipping|skipped' {
            $type = 'SKIPPED'
            $desc = 'Action skipped (Already Exists) (¬_¬")'
            break
        }
        '❌|error|failed|denied|missing' {
            $type = 'ERROR'
            $desc = 'An error occurred ¯\_(ಠ_ಠ)_/¯'
            break
        }
        '🔹|starting|connecting|running|info|note' {
            $type = 'INFO'
            $desc = 'Informational message (•_•)ノ⌒💡'
            break
        }
        '🔄|processing|updating|working|waiting' {
            $type = 'PROCESSING'
            $desc = 'Processing... (•_•)>⌛'
            break
        }
        '🎉|finished|complete|completed' {
            $type = 'COMPLETED'
            $desc = 'All operations finished ヽ(•‿•)ノ🎉'
            break
        }
        default {
            $type = 'GENERAL'
            $desc = 'General message (•_•)'
        }
    }

    return @{
        Type        = $type
        Description = $desc
        Text        = $Text
    }
}

# ========================================
# ||          Generic Runner            ||
# ========================================

function Run-ExternalScript {
    param(
        [Parameter(Mandatory)]
        [string]$ScriptPath,
        [System.Windows.Forms.TextBox]$OutputBox
    )

    # Fallback for console use
    if (-not $OutputBox) {
        $OutputBox = New-Object PSObject
        $OutputBox | Add-Member ScriptMethod AppendText { param($t) Write-Host $t }
        $OutputBox | Add-Member NoteProperty ForeColor ([System.Drawing.Color]::Lime)
    }

    # Validate the script path
    if (-not (Test-Path $ScriptPath)) {
        $OutputBox.AppendText("[ERROR] Script not found at $ScriptPath`r`n")
        return
    }

    try {
        # Run script and stream output line-by-line
        & $ScriptPath 2>&1 | Out-String -Stream | ForEach-Object {
            $lineInfo = Format-ColoredLine $_

            # Choose readable prefixes
            switch ($lineInfo.Type) {
                'SUCCESS'    { $prefix = '[SUCCESS]' }
                'SKIPPED'    { $prefix = '[SKIPPED]' }
                'ERROR'      { $prefix = '[ERROR]' }
                'INFO'       { $prefix = '[INFO]' }
                'PROCESSING' { $prefix = '[PROCESS]' }
                'COMPLETED'  { $prefix = '[DONE]' }
                default      { $prefix = '[LOG]' }
               }


            # Add timestamp AFTER the tag
            $time = (Get-Date).ToString('HH:mm:ss')
            $OutputBox.AppendText("$prefix [$time] $($lineInfo.Text)`r`n")
            $OutputBox.ScrollToCaret()
        }

        Start-Sleep -Milliseconds 300
        $OutputBox.ForeColor = [System.Drawing.Color]::Lime
        $OutputBox.ScrollToCaret()
    }
    catch {
        $OutputBox.AppendText("[ERROR] Failed to run script: $($_.Exception.Message)`r`n")
    }
}
 
