function Show-AzureWindow {
    # Create the main form (window) for the Azure launcher
    $azureForm = New-Object System.Windows.Forms.Form
    Set-WindowIcon $azureForm
    $azureForm.ShowIcon = $true
    $azureForm.Text = "Azure Script Launcher"                        # Window title
    $azureForm.StartPosition = "CenterScreen"                        # Open in center of screen
    $azureForm.BackColor = [System.Drawing.Color]::WhiteSmoke        # Background color
    $azureForm.Size = New-Object System.Drawing.Size(900,900)        # Default window size (W,H)

    # Main layout with 2 rows (row 0 = buttons, row 1 = output + back)
    $layout = New-Object System.Windows.Forms.TableLayoutPanel
    $layout.Dock = "Fill"
    $layout.RowCount = 2
    $layout.ColumnCount = 1
    $layout.RowStyles.Add((New-Object System.Windows.Forms.RowStyle(
        [System.Windows.Forms.SizeType]::AutoSize)))                 # Row 0 auto-sizes for buttons
    $layout.RowStyles.Add((New-Object System.Windows.Forms.RowStyle(
        [System.Windows.Forms.SizeType]::Percent,100)))              # Row 1 fills remaining space
    $azureForm.Controls.Add($layout)

    # ---------- Row 0: Script Buttons ----------
    $buttonPanel = New-Object System.Windows.Forms.FlowLayoutPanel
    $buttonPanel.FlowDirection = "TopDown"                           # Stack buttons vertically
    $buttonPanel.Dock = "Top"
    $buttonPanel.AutoSize = $true
