function Show-IntuneWindow {
    # --- Create the form (window) for Intune management ---
    $intuneForm = New-Object System.Windows.Forms.Form
    Set-WindowIcon $intuneForm
    $intuneForm.ShowIcon = $true
    $intuneForm.Text = "Intune Script Launcher"                       # Window title
    $intuneForm.StartPosition = "CenterScreen"                        # Open in the center of screen
    $intuneForm.BackColor = [System.Drawing.Color]::WhiteSmoke         # Light background
    $intuneForm.Size = New-Object System.Drawing.Size(1700,800)         # Default size

     # --- Menu Bar ---
    $menuStrip = New-Object System.Windows.Forms.MenuStrip

    # "Tools" menu
    $toolsMenu = New-Object System.Windows.Forms.ToolStripMenuItem
    $toolsMenu.Text = "Tools"

    # "Download Win32 Tool" item
    $downloadItem = New-Object System.Windows.Forms.ToolStripMenuItem
    $downloadItem.Text = "Download Win32 Tool"

    # Add item to Tools
    $toolsMenu.DropDownItems.Add($downloadItem)

    # Add Tools to menu bar
    $menuStrip.Items.Add($toolsMenu)

    # Attach menu to form
    $intuneForm.MainMenuStrip = $menuStrip
    $intuneForm.Controls.Add($menuStrip)
    $contentPanel = New-Object System.Windows.Forms.Panel
    $contentPanel.Dock = "Fill"
    $contentPanel.Padding = New-Object System.Windows.Forms.Padding(0,25,0,0)   # space so menu doesn't overlap
    $intuneForm.Controls.Add($contentPanel)

        $downloadItem.Add_Click({
        try {
            $desktop = [Environment]::GetFolderPath("Desktop")
            $sourceFile = Join-Path $basePath "Intune\Win32_Tool.exe"
            $targetFile = Join-Path $desktop "Win32_Tool.exe"

            Copy-Item -Path $sourceFile -Destination $targetFile -Force

            [System.Windows.Forms.MessageBox]::Show("Win32 Tool copied to Desktop.","Completed")
        }
        catch {
            [System.Windows.Forms.MessageBox]::Show("Copy failed: $($_.Exception.Message)","Error")
        }
    })


    # --- Main layout with 3 rows ---
    $layout = New-Object System.Windows.Forms.TableLayoutPanel
    $layout.Dock = "Fill"
    $layout.RowCount = 3
    $layout.ColumnCount = 1
    $layout.RowStyles.Add((New-Object System.Windows.Forms.RowStyle([System.Windows.Forms.SizeType]::AutoSize)))   # Row 0: Android + Apple
