    $bottomPanel.Dock = "Fill"
    $bottomPanel.RowCount = 2
    $bottomPanel.ColumnCount = 1
    $bottomPanel.RowStyles.Add((New-Object System.Windows.Forms.RowStyle([System.Windows.Forms.SizeType]::Percent, 100))) # Output fills space
    $bottomPanel.RowStyles.Add((New-Object System.Windows.Forms.RowStyle([System.Windows.Forms.SizeType]::AutoSize)))     # Back button auto-size
    $layout.Controls.Add($bottomPanel,0,2)

    # Output text box (console-like log window)
    $outputBox = New-Object System.Windows.Forms.TextBox
    $outputBox.Multiline = $true
    $outputBox.ScrollBars = "Vertical"
    $outputBox.ReadOnly = $true
    $outputBox.Font = New-Object System.Drawing.Font("Consolas", 10)
    $outputBox.Dock = "Fill"
    $outputBox.BackColor = [System.Drawing.Color]::Black
    $outputBox.ForeColor = [System.Drawing.Color]::Lime
    $bottomPanel.Controls.Add($outputBox,0,0)

