    $btnBack.Text = "← Back"
    $btnBack.Dock = "Fill"
    $btnBack.Height = 40
    $btnBack.BackColor = [System.Drawing.Color]::LightCoral
    $btnBack.ForeColor = [System.Drawing.Color]::White
    $btnBack.Font = New-Object System.Drawing.Font("Segoe UI", 10, [System.Drawing.FontStyle]::Bold)
