        Show-MainWindow           # Show Main window again
        $intuneForm.Dispose()     # Dispose to free resources
    })
    $bottomPanel.Controls.Add($btnBack,0,1)

    # ---------- Helper: Category Label ----------
    function New-CategoryLabel($text) {
        $lbl = New-Object System.Windows.Forms.Label
        $lbl.Text = $text
        $lbl.Font = New-Object System.Drawing.Font("Segoe UI", 11, [System.Drawing.FontStyle]::Bold)
