        $lbl.AutoSize = $true
        $lbl.Padding = New-Object System.Windows.Forms.Padding(0,8,0,4)  # Top & bottom spacing
        return $lbl
    }

    # ========== Row 0: Android + Apple ==========
    $row1 = New-Object System.Windows.Forms.TableLayoutPanel
    $row1.Dock = "Top"
    $row1.ColumnCount = 2
    $row1.AutoSize = $true
    $row1.ColumnStyles.Add((New-Object System.Windows.Forms.ColumnStyle([System.Windows.Forms.SizeType]::Percent, 50)))
    $row1.ColumnStyles.Add((New-Object System.Windows.Forms.ColumnStyle([System.Windows.Forms.SizeType]::Percent, 50)))

    # --- Android Panel ---
    $androidPanel = New-Object System.Windows.Forms.FlowLayoutPanel
    $androidPanel.FlowDirection = "TopDown"; $androidPanel.AutoSize = $true
    $androidPanel.Controls.Add((New-CategoryLabel "Android"))
    $androidPanel.Controls.Add((Invoke-Pwsh5Script "Android Mobile Groups"  (Join-Path $basePath "Intune\Setup_Android_Mobile_Devices.ps1") $outputBox))
    $androidPanel.Controls.Add((Invoke-Pwsh5Script "Android Tablets Groups" (Join-Path $basePath "Intune\Setup_Android_Tablets_Devices.ps1") $outputBox))
        
    # --- Apple Panel ---
    $applePanel = New-Object System.Windows.Forms.FlowLayoutPanel
    $applePanel.FlowDirection = "TopDown"; $applePanel.AutoSize = $true
    $applePanel.Controls.Add((New-CategoryLabel "Apple"))
    $applePanel.Controls.Add((Invoke-Pwsh5Script "iPad Groups" (Join-Path $basePath "Intune\Setup_iPad_Devices.ps1") $outputBox))
    $applePanel.Controls.Add((Invoke-Pwsh5Script "Mac Groups"  (Join-Path $basePath "Intune\Setup_MAC_Devices.ps1") $outputBox))
        
    $row1.Controls.Add($androidPanel,0,0)
    $row1.Controls.Add($applePanel,1,0)
    $layout.Controls.Add($row1,0,0)

    # ========== Row 1: Other + Windows ==========
    $row2 = New-Object System.Windows.Forms.TableLayoutPanel
    $row2.Dock = "Top"
    $row2.ColumnCount = 2
    $row2.AutoSize = $true
    $row2.ColumnStyles.Add((New-Object System.Windows.Forms.ColumnStyle([System.Windows.Forms.SizeType]::Percent, 50)))
    $row2.ColumnStyles.Add((New-Object System.Windows.Forms.ColumnStyle([System.Windows.Forms.SizeType]::Percent, 50)))

    # --- Other Panel ---
    $otherPanel = New-Object System.Windows.Forms.FlowLayoutPanel
    $otherPanel.FlowDirection = "TopDown"; $otherPanel.AutoSize = $true
    $otherPanel.Controls.Add((New-CategoryLabel "Other"))
    $otherPanel.Controls.Add((Invoke-Pwsh5Script "Exam User Groups" (Join-Path $basePath "Intune\Intune Exam User Groups.ps1") $outputBox))
    
    # --- Windows Panel ---
    $windowsPanel = New-Object System.Windows.Forms.FlowLayoutPanel
    $windowsPanel.FlowDirection = "TopDown"; $windowsPanel.AutoSize = $true
    $windowsPanel.Controls.Add((New-CategoryLabel "Windows"))
    $windowsPanel.Controls.Add((Invoke-Pwsh5Script "Device Groups" (Join-Path $basePath "Intune\Setup_Windows_Devices.ps1") $outputBox))
    
    $row2.Controls.Add($otherPanel,0,0)
    $row2.Controls.Add($windowsPanel,1,0)
    $layout.Controls.Add($row2,0,1)

    # --- Show form as modal dialog ---
    [void]$intuneForm.ShowDialog()
}

