            $btnFlow = New-Object System.Windows.Forms.FlowLayoutPanel
            $btnFlow.FlowDirection = "TopDown"
            $btnFlow.WrapContents = $false
            $btnFlow.AutoScroll = $true
            $btnFlow.Dock = "Fill"
            $panel.Controls.Add($btnFlow,0,1)

            return @{ Panel=$panel; Buttons=$btnFlow }
        }

        # === Sections ===
