# ============================================================
# ||                     Main Window                        ||
# ============================================================
function Show-MainWindow {
    # ============================================================
    # ||              Create the Main Launcher Form             ||
    # ============================================================
    $mainWindowsForm = New-Object System.Windows.Forms.Form
    Set-WindowIcon $mainWindowsForm
    $mainWindowsForm.ShowIcon = $true
    $mainWindowsForm.Text = "CloudDonny Script Launcher"             # Window title
    $mainWindowsForm.StartPosition = "CenterScreen"                  # Open in center of screen
    $mainWindowsForm.BackColor = [System.Drawing.Color]::WhiteSmoke  # Background color
    $mainWindowsForm.Size = New-Object System.Drawing.Size(1000, 480)# Default window size (W,H)
    $mainWindowsForm.MinimumSize = New-Object System.Drawing.Size(850, 400) # Prevent resizing too small

    # ============================================================
    # ||                    Menu Bar (Top)                      ||
    # ============================================================

    # Create the menu bar
    $menuStrip = New-Object System.Windows.Forms.MenuStrip

    # Create "Help" menu and its items
    $helpMenu      = New-Object System.Windows.Forms.ToolStripMenuItem "Help Im A Noob"
    $userGuideItem = New-Object System.Windows.Forms.ToolStripMenuItem "Download User Guide"
    
    # Define paths relative to where the script is running
    $userGuidePath = Join-Path $basePath "UserGuide.pdf"
    
    # --- Action for "Download User Guide" ---
    $userGuideItem.Add_Click({
        if (Test-Path $userGuidePath) {
            Start-Process $userGuidePath  # Open PDF in default viewer
        } else {
            [System.Windows.Forms.MessageBox]::Show("❌ User Guide not found at:`n$userGuidePath")
        }
    })

    # Add items under Help menu
    $helpMenu.DropDownItems.AddRange(@($userGuideItem))
    $menuStrip.Items.Add($helpMenu)

    # Attach menu strip to the form
    $mainWindowsForm.MainMenuStrip = $menuStrip
    $mainWindowsForm.Controls.Add($menuStrip)

    # ============================================================
    # ||        Container Panel for Layout (below menu)          ||
    # ============================================================

    # Create a panel that will sit below the menu bar
    $mainPanel = New-Object System.Windows.Forms.Panel
    $mainPanel.Dock = "Fill"   # Fill everything below menu
    $mainPanel.Padding = New-Object System.Windows.Forms.Padding(0,25,0,0)  
    $mainWindowsForm.Controls.Add($mainPanel)

    # ============================================================
    # ||                Main Section Grid Layout                ||
    # ============================================================

    # Create a 3x2 grid layout for section buttons
    $layout = New-Object System.Windows.Forms.TableLayoutPanel
    $layout.Dock = "Fill"
    $layout.ColumnCount = 3
    $layout.RowCount = 2
    $layout.Padding = New-Object System.Windows.Forms.Padding(14)

    # Equal column widths (3 columns, 33.3% each)
    for ($i=0; $i -lt 3; $i++) {
        $layout.ColumnStyles.Add(
            (New-Object System.Windows.Forms.ColumnStyle(
                [System.Windows.Forms.SizeType]::Percent, 33.33))
        )
    }

    # Equal row heights (2 rows, 50% each)
    for ($i=0; $i -lt 2; $i++) {
        $layout.RowStyles.Add(
            (New-Object System.Windows.Forms.RowStyle(
                [System.Windows.Forms.SizeType]::Percent, 50))
        )
    }

    # Add the layout to the mainPanel (not directly to form)
    $mainPanel.Controls.Add($layout)

    # ============================================================
    # ||                  Section Buttons                       ||
    # ============================================================

    $sections = @("Azure","Entra","Intune","OneDrive/Teams","O365","SharePoint")

    $row = 0; $col = 0
    foreach ($section in $sections) {
        # Create a styled button
        $btn = New-Object System.Windows.Forms.Button
        $btn.Text = $section
        $btn.Dock = "Fill"
        $btn.Margin = New-Object System.Windows.Forms.Padding(8)
        $btn.FlatStyle = [System.Windows.Forms.FlatStyle]::System
        $btn.Font = New-Object System.Drawing.Font("Segoe UI", 12, [System.Drawing.FontStyle]::Bold)
        $btn.BackColor = [System.Drawing.Color]::CornflowerBlue
        $btn.ForeColor = [System.Drawing.Color]::White

        # Assign actions per section
        switch ($section) {
            "Intune"           { $btn.Add_Click({ $mainWindowsForm.Hide(); Show-IntuneWindow          -basePath $basePath; $mainWindowsForm.Dispose() }) }
            "O365"             { $btn.Add_Click({ $mainWindowsForm.Hide(); Show-O365Window            -basePath $basePath; $mainWindowsForm.Dispose() }) }
            "Entra"            { $btn.Add_Click({ $mainWindowsForm.Hide(); Show-EntraWindow           -basePath $basePath; $mainWindowsForm.Dispose() }) }
            "Azure"            { $btn.Add_Click({ $mainWindowsForm.Hide(); Show-AzureWindow           -basePath $basePath; $mainWindowsForm.Dispose() }) }
            "OneDrive/Teams"   { $btn.Add_Click({ $mainWindowsForm.Hide(); Show-OneDriveTeamsWindow   -basePath $basePath; $mainWindowsForm.Dispose() }) }
            "SharePoint"       { $btn.Add_Click({ $mainWindowsForm.Hide(); Show-SharePointWindow      -basePath $basePath; $mainWindowsForm.Dispose() }) }
        }

        # Place button in grid
        $layout.Controls.Add($btn, $col, $row)

        # Move grid position
        $col++
        if ($col -ge 3) { $col = 0; $row++ }
    }

    # ============================================================
    # ||              Display the Main Window                   ||
    # ============================================================
        # Prevent initial blue focus on first button (e.g., Azure)
    $mainWindowsForm.Add_Shown({
        $mainWindowsForm.ActiveControl = $null
    })

    [void]$mainWindowsForm.ShowDialog()
}

